 } else {      currVid++;      }    tv.loadVideoById(vid[currVid]);    tv.seekTo(vid[currVid].startSeconds);  }}function vidRescale(){  var w = $(window).width()+200,    h = $(window).height()+200;  if (w/h > 16/9){    tv.setSize(w, w/16*9);    $('.tv .screen').css({'left': '0px'});  } else {    tv.setSize(h/9*16, h);    $('.tv .screen').css({'left': -($('.tv .screen').outerWidth()-w)/2});  }}$(window).on('load resize', function(){  vidRescale();});$('.hi span:first-of-type').on('click', function(){  $('#tv').toggleClass('mute');  $('.hi em:first-of-type').toggleClass('hidden');  if($('#tv').hasClass('mute')){    tv.mute();  } else {    tv.unMute();  }});$('.hi span:last-of-type').on('click', function(){  $('.hi em:nth-of-type(2)').html('~');  tv.pauseVideo();});</script>    <?php      do_action('get_header');      get_template_part('templates/header');    ?>        <?php if(true === get_theme_mod('homepage_slider')): ?>     <div class="container">        <?php get_template_part('homepageimageslider'); ?>                </div>        <?php else: ?>        <div class="welcome-home">    <div class="container" id="welcome-text">            <div class="welctxt" id="welcometext">        <h3>Welcome <?php if ( get_theme_mod( 'club_initials' ) ) : ?> to   <?php echo esc_html(get_theme_mod('club_initials','')); ?>   <?php endif; ?></h3>     <p><?php if ( get_theme_mod( 'club_welcome' ) ) : ?>     <?php echo esc_html(get_theme_mod('club_welcome','')); ?>  <?php else: ?> Welcome to the official club website of <?php bloginfo( 'name' ); ?>.    <?php endif; ?></p>                    </div>            <?php get_template_part('homepage/slider'); ?>        </div>    </div>        <?php endif; ?>        <div class="wrap" style="margin-top: 0px !important; margin-bottom: 0px !important; padding-bottom: 0px !important; padding-top: 0px !important; background: transparent !important;"><div class="contain