<?php/*Template Name: Club Events Page*/?><p>A list of upcoming and past events and matches, excluding any games uploaded to external league websites such as FA Full-Time.</p>        <?php //Set server timezone to GMTdate_default_timezone_set('Europe/London'); //Today's date$date_1 = date('Ymd', strtotime("now")); //Future date - the arg will look between today's date and this future date to see if the post fall within the 2 dates.$date_2 = date('Ymd', strtotime("+1 week"));?><?php                $paged = get_query_var('paged') ? get_query_var('paged') : 1;                $args = array(                'post_type' => array( 'event', 'wpcm_match' ),                'posts_per_page' => 5,                'date_query' => array(        array(            'after' => date( 'Y-m-d h:i', time() ),        )    ),                'orderby' => 'date',                'paged' => $paged,                'max_num_pages' => 10                );        ?>              <?php $wp_query = new WP_Query($args); ?>         <?php if ( $wp_query->have_posts() ) : ?>         <?php while( $wp_query->have_posts() ) : $wp_query->the_post(); ?>                <div id="upcevents" style="position: relative; padding-left: 47px;">                        <?php $date = date_i18n( get_option( 'date_format' ), strtotime( $post->post_date ) ); ?>                <style>#upcevents h2 {margin-bottom: 5px !important;} #upcevents p {margin-top: 7px;} .eventsnavigation-list a:last-child { text-align: right !important; } .eventsnavigation-list {clear: both; display: block; position: relative;} .eventsnavigation-list a {width: 50%; display: block; float: left;} .fixed-event-date {background: grey; border-radius: 50%; width: 33.5px; height: 33.5px; color: #fff; text-align: center; position: absolute; left: 0px; top: calc(50% - 15.5px);} .fixed-event-date {display: flex; align-items: center; justify-content: center;}</style>                    <span class="fixed-event-date"><div><?php the_time('j') ?></div></span>               <h2><a href="<?php echo get_post_permalink( $post->ID, false, true ); ?>"><?php the_title(); ?></a></h2>                <?php the_post_thumbnail(); ?>                  <?php the_excerpt(); ?>                 <p> <i style="margin-right: 3.5px;" class="fa fa-calendar-o" aria-hidden="true"></i> <strong>Date:</strong>                                 <?php if( get_post_type() == 'event' ) : ?>                                <?php $date = date_i18n( get_option( 'date_format' ), strtotime( $post->post_date ) );                $time = date_i18n( get_option( 'time_format' ), strtotime( $post->post_date ) ); ?>                                <time class="updated" datetime="<?php echo $date; ?>"> <?php echo $date; ?> @ <?php echo $time; ?></time>                                                <?php elseif( get_post_type() == 'wpcm_match' ) : ?>                                <?php $date = date_i18n( get_option( 'date_format' ), strtotime( $post->post_date ) );                $time = date_i18n( get_option( 'time_format' ), strtotime( $post->post_date ) ); ?>                                <time class="updated" datetime="<?php echo $date; ?>"> <?php echo $date; ?> @ <?php echo $time; ?></time>                                <?php endif; ?>                </p>                        </div>         <?php endwhile; ?>                       <div class="eventsnavigation-list">                <?php posts_nav_link(' ','Newer Events','Older Events'); ?>                        </div>          <?php else: ?>        <p><?php _e('Sorry, there are no upcoming club events.'); ?></p>        <?php endif; ?>